mapper.select:
SET @next_msg_id := (
    select IFNULL(max(msg_id),0)+1
    from tb_alimtalk_history
    where shop_id=#{currShopId} and
          sale_id=#{saleId} and
          rsv_dt=#{rsvDt}
    );

INSERT INTO tb_alimtalk_history
(shop_id, sale_id, msg_id, tpl_id, content, rsv_dt, rsv_tp, dday)
VALUES (#{currShopId}, #{saleId}, @next_msg_id, #{tplId},#{content},#{rsvDt}, #{rsvTp}, #{dday});

SELECT @next_msg_id

mapper.insert:
SET @next_msg_id := (
    select IFNULL(max(msg_id),0)+1
    from tb_alimtalk_history
    where shop_id=#{currShopId} and
        sale_id=#{saleId}
);

INSERT INTO tb_alimtalk_history
    (shop_id, sale_id, msg_id, tpl_id, content, rsv_dt, rsv_tp, dday)
VALUES

        (#{currShopId}, #{saleId}, @next_msg_id + #{index}, #{item.tplId},#{item.content},#{item.rsvDt}, #{item.rsvTp}, #{item.dday})

mapper.delete:
delete from tb_alimtalk_history
where
(

    (
        shop_id=#{item.shopId} and
        sale_id=#{item.saleId} and
        rsv_dt=#{item.rsvDt} and
        msg_id=#{item.msgId}
    )

)

mapper.select:
set @from := ( select concat(#{date},'-01') );
with recursive calendar as ( select @from as d union all select date_add(d, interval 1 day) as d from calendar where d < last_day(@from) ), msg as ( select rsv_dt, json_object( 'msg_st',msg_st, 'cnt', IFNULL(count(msg_st),0) ) as value from tb_alimtalk_history where shop_id=#{currShopId} group by rsv_dt, msg_st )
select json_arrayagg(value) as value from calendar left outer join msg rm on rm.rsv_dt=d group by d

mapper.update:
update tb_alimtalk_history
set msg_st=#{msgSt}
where shop_id=#{shopId} and
      sale_id=#{saleId} and
      rsv_dt=#{rsvDt} and
      msg_id=#{msgId}

mapper.select:
select *,
       IF(tah.tpl_id is null, true, false) as reserved
from tb_alimtalk_template tat
left outer join tb_alimtalk_history tah on tah.tpl_id=tat.tpl_id
and tah.shop_id=#{shopId} and tah.sale_id=#{saleId}
where grp_code=#{grpCode}

select tpl_code
from tb_alimtalk_template
where tpl_id=#{tplId}
