mapper.select:
with base as ( select * from tb_device ), search as ( select * from base where
( device_cd like '%${keyword}%' or device_nm like '%${keyword}%' ) and
provider = #{provider} and
1=1
order by ${order} ${asc}
limit ${limit}
), result as ( select JSON_ARRAYAGG(json_object( 'id',device_id, 'name',device_nm, 'code',device_cd, 'provider',provider )) from search )
select ( select IFNULL(count(*),0) from base ) as total_cnt, ( select * from result ) as list

with base as ( select * from tb_second_device ), search as ( select * from base where
( sd_cd like '%${keyword}%' or sd_nm like '%${keyword}%' ) and
provider = #{provider} and
1=1
order by ${order} ${asc}
limit ${limit}
), result as ( select JSON_ARRAYAGG(JSON_OBJECT( 'id',sd_id, 'name',sd_nm, 'code',sd_cd, 'provider',provider )) from search
select ( select IFNULL(count(*),0) from base ) as total_cnt, ( select * from result ) as list

with base as (
    select *
    from tb_ct_plan
), search as (
    select *
    from    base
    where

        (
        ct_plan_nm like '%${keyword}%'
        ) and


        provider = #{provider} and

    1=1

        order by ${order} ${asc}


        limit ${limit}

), result as (
    select json_arrayagg(json_object(
    'id',ct_plan_id,
    'name',ct_plan_nm,
    'provider',provider
    ))
    from    search
)

select (
select IFNULL(count(*),0)
from base
) as total_cnt,
(
select *
from result
) as list

with base as (
    select *
    from tb_extra_service
    where shop_id=#{currShopId}
), search as (
