mapper.insert:
set @next_id := (
    select IFNULL(max(device_id),0)+1
    from tb_device
);

insert into tb_device
(device_id, provider, device_nm, device_cd)
VALUES

    (@next_id+#{index}),#{item.provider},#{item.name},#{item.code}

set @next_id := (
select IFNULL(max(sd_id),0)+1
from tb_second_device
);

insert into tb_second_device
(sd_id, provider, sd_nm, sd_cd)
VALUES

    (@next_id+#{index}),#{item.provider},#{item.name},#{item.code}

 set @next_id := (
 select IFNULL(max(ct_plan_id),0)+1
 from tb_ct_plan
 );

 insert into tb_ct_plan
(ct_plan_id, provider, ct_plan_nm)
 VALUES

     (@next_id+#{index}),#{item.provider},#{item.name}

set @next_id := (
select IFNULL(max(internet_plan_id),0)+1
from tb_internet_plan
);

insert into tb_internet_plan
(shop_id, internet_plan_id, provider, internet_plan_nm)
VALUES

    #{currShopId},(@next_id+#{index}),#{item.provider},#{item.name}

set @next_id := (
select IFNULL(max(tv_plan_id),0)+1
from tb_tv_plan
);

insert into tb_tv_plan
(shop_id, tv_plan_id, provider, tv_plan_nm)
VALUES

    #{currShopId},(@next_id+#{index}),#{item.provider},#{item.name}

set @next_id := (
select IFNULL(max(exsvc_id),0)+1
from tb_extra_service
);

insert into tb_extra_service
(shop_id, exsvc_id, provider, exsvc_nm)
VALUES

    #{currShopId},(@next_id+#{index}),#{item.provider},#{item.name}

set @next_id := (
select IFNULL(max(sup_div_id),0)+1
from tb_sup_div
);

insert into tb_sup_div
(shop_id, sup_div_id, sup_div_nm)
VALUES

    #{currShopId},(@next_id+#{index}),#{item.name}

set @next_id := (
select IFNULL(max(add_div_id),0)+1
from tb_add_div
);

insert into tb_add_div
(shop_id, add_div_id, add_div_nm)
VALUES

    #{currShopId},(@next_id+#{index}),#{item.name}

set @next_id := (
select IFNULL(max(tv_plan_id),0)+1
from tb_comb_tp
);

insert into tb_comb_tp
(shop_id, comb_tp_id, comb_tp_nm)
VALUES

    #{currShopId},(@next_id+#{index}),#{item.name}

mapper.delete:
delete from tb_internet_plan
where shop_id=#{currShopId} and
(

    internet_plan_id=#{item}

)

delete from tb_tv_plan
where shop_id=#{currShopId} and
(

    tv_plan_id=#{item}

)

delete from tb_sup_div
where shop_id=#{currShopId} and
(

    sup_div_id=#{item}

)

delete from tb_add_div
where shop_id=#{currShopId} and
(

    add_div_id=#{item}

)

delete from tb_extra_service
where shop_id=#{currShopId} and
(

    exsvc_id=#{item}

)

delete from tb_comb_tp
where shop_id=#{currShopId} and
(

    comb_tp_id=#{item}

)

mapper.update:
update tb_device
set provider=#{provider},
    device_cd=#{code},
    device_nm=#{name}
where device_id=#{id}

update tb_second_device
set provider=#{provider},
    sd_cd=#{code},
    sd_nm=#{name}
where sd_id=#{id}

update tb_ct_plan
set provider=#{provider},
    ct_plan_nm=#{name}
where ct_plan_id=#{id}

update tb_internet_plan
set provider=#{provider},
    internet_plan_nm=#{name}
where internet_plan_id=#{id} and
      shop_id=#{currShopId}

update tb_tv_plan
set provider=#{provider},
    tv_plan_nm=#{name}
where tv_plan_id=#{id} and
    shop_id=#{currShopId}

update tb_sup_div
set sup_div_nm=#{name}
where sup_div_id=#{id} and
    shop_id=#{currShopId}

update tb_add_div
set add_div_nm=#{name}
where add_div_id=#{id} and
    shop_id=#{currShopId}

update tb_extra_service
set provider=#{provider},
    exsvc_nm=#{name}
where exsvc_id=#{id} and
    shop_id=#{currShopId}

update tb_comb_tp
set comb_tp_nm=#{name}
where comb_tp_id=#{id} and
    shop_id=#{currShopId}
