mapper.select:
with tb as ( select s.shop_cd as shop_cd, s.shop_nm as shop_nm, mr.sale_no as sale_no, mr.send_tp as send_tp, mr.send_st as send_st, mr.cust_nm as cust_nm, mr.cust_tel as cust_tel, mr.content as content, mr.seller_id as seller_id, a.name as seller_nm, mr.rsv_dt as rsv_dt, mr.regi_dt as regi_dt from tb_msg_rsv mr left outer join tb_shop s on mr.shop_cd=s.shop_cd left outer join tb_account a on mr.seller_id=a.id )
select * from tb where ${orSearch} and ${andSelect}
order by ${orderby} ${side}
limit ${offset},${limit}
