mapper.insert:
SET @curr_shop_id := (
    select curr_shop_id
    from tb_account
    where id=#{userId}
    );

SET @next_msg_id := (
    select IFNULL(max(msg_id),0)+1
    from tb_rsv_msg
    where shop_id=@curr_shop_id and
          sale_id=#{saleId}
    );

INSERT INTO tb_rsv_msg
(shop_id, sale_id, msg_id, msg_tp, content, rsv_dt)
VALUES (@curr_shop_id, #{saleId}, @next_msg_id, #{msgTp},#{content},#{rsvDt})
