mapper.select:
select count(sale_id)
from tb_sale
where shop_id=#{currShopId}

mapper.update:
UPDATE tb_promise
SET checked=#{checked}
WHERE shop_id= #{currShopId} and
    sale_id=#{saleId} and
    pm_id=#{pmId}

UPDATE tb_sale_used_device
SET ud_st=#{state}
WHERE shop_id= #{currShopId} and
    sale_id=#{saleId} and
    ud_id=#{udId}

UPDATE tb_sale_card
SET card_st=#{state}
WHERE shop_id=#{currShopId} and
    sale_id=#{saleId} and
    card_id=#{cardId}

UPDATE tb_sale
SET comb_st=#{state}
WHERE shop_id= #{currShopId} and
    sale_id=#{saleId}

UPDATE tb_sale_support
SET sup_st=#{state}
WHERE shop_id = #{currShopId} and
    sale_id=#{saleId} and
    sup_id=#{supId}

mapper.delete:
delete from tb_sale
where sale_id=#{saleId}
and shop_id= #{currShopId}

mapper.select:
SELECT IFNULL(max(sale_id),0)
from tb_sale
WHERE shop_id= #{currShopId}

mapper.update:
UPDATE tb_promise
SET checked=#{checked},
    content=#{content}
WHERE shop_id = #{currShopId} and
    sale_id=#{saleId} and
    pm_id=#{pmId}

UPDATE tb_sale_support
SET sup_div=#{div},
    sup_amount=#{amount}
WHERE shop_id = #{currShopId} and
      sale_id=#{saleId} and
      sup_id=#{supId}

UPDATE tb_sale_add
SET add_div=#{div},
    add_amount=#{amount}
WHERE shop_id = #{currShopId} and
    sale_id=#{saleId} and
    add_id=#{addId}

UPDATE tb_sale_card
SET card_nm=#{cardNm},
    card_tp=#{cardTp}
WHERE shop_id = #{currShopId} and
    sale_id=#{saleId} and
    card_id=#{cardId}

UPDATE tb_sale_used_device
SET ud_nm=#{udNm},
    ud_stor=#{udStor}
WHERE shop_id = #{currShopId} and
    sale_id=#{saleId} and
    ud_id=#{udId}

mapper.select:
SELECT count(*)
from tb_promise
where shop_id = #{currShopId}

SELECT count(*)
from tb_sale_support
where shop_id = #{currShopId}

SELECT count(*)
from tb_sale_card
where shop_id = #{currShopId}

SELECT count(*)
from tb_sale_used_device
where shop_id = #{currShopId}

mapper.delete:
DELETE FROM tb_promise
WHERE shop_id= #{currShopId} and
    sale_id=#{saleId}

DELETE FROM tb_sale_support
WHERE shop_id= #{currShopId} and
    sale_id=#{saleId}

DELETE FROM tb_sale_add
WHERE shop_id= #{currShopId} and
    sale_id=#{saleId}

DELETE FROM tb_sale_card
WHERE shop_id= #{currShopId} and
    sale_id=#{saleId}

DELETE FROM tb_sale_used_device
WHERE shop_id= #{currShopId} and
    sale_id=#{saleId}

mapper.select:
SET @total_sale_count := ( SELECT count(*) FROM tb_sale WHERE shop_id=#{currShopId} and actv_dt like '${date}%' );
with tb as ( SELECT count(*) as curr FROM tb_sale_card sc LEFT OUTER JOIN tb_sale sl on sl.sale_id=sc.sale_id WHERE sc.shop_id=#{currShopId} and actv_dt like '${date}%' UNION ALL SELECT count(*) as curr FROM tb_sale WHERE shop_id= #{currShopId} and actv_dt like '${date}%' and sd_id != 0 UNION ALL SELECT count(*) as curr FROM tb_sale WHERE shop_id= #{currShopId} and actv_dt like '${date}%' and exsvc_id != 0 )
SELECT curr as value, @total_sale_count as total, ( CASE WHEN @total_sale_count = 0 THEN 100 WHEN @total_sale_count != 0 THEN TRUNCATE(((curr/@total_sale_count))*100, 1) END ) as pct FROM tb

SET @org_month := ( SELECT MONTH(#{fromYmd}) );
with recursive date_range as ( select #{fromYmd} as d union all select date_add(d, interval 1 day) as d from date_range where MONTH(d) != @org_month )
SELECT ( SELECT device_nm FROM tb_device WHERE tb_device.device_id=sl.device_id ) as device_nm, TRUNCATE((IFNULL(count(device_id),0) / IFNULL(count(*), 1)), 1) as per FROM date_range LEFT OUTER JOIN tb_sale sl on sl.actv_dt=d and shop_id=#{currShopId} and device_id != 0 group by device_id
