mapper.select:
with sale as ( select s.*, IF(device_id is not null, true, false) as ct, IF(wt_cms > 0, true, false) as wt, IF(s.sd_id is not null, true, false) as sd, ( select device_nm from tb_device where device_id=s.device_id ) as device_nm, ac.name as seller_nm, ac.pfp as seller_pfp from tb_sale s left outer join tb_account ac on s.seller_id=ac.id where shop_id=#{currShopId}
order by ${order} ${asc}
), search as ( select json_arrayagg(json_object( 'sale_id',sale_id, 'cust_nm', cust_nm, 'cust_tel',cust_tel, 'cust_cd',cust_cd, 'cust_gd',cust_gd, 'actv_dt',actv_dt, 'total_cms',total_cms, 'ct', ct, 'wt',wt, 'sd',sd, 'device_nm',device_nm, 'seller_nm',seller_nm, 'seller_pfp', seller_pfp )) from sale where
( cust_nm like '%${keyword}%' or cust_tel like '%${keyword}%' or cust_cd like '%${keyword}%' ) and
( actv_dt like '%${keydate}%' ) and
${filters} 1=1
)
select ( select count(*) from sale ) as total_cnt, ( select * from search ) as list

mapper.update:
UPDATE tb_promise
SET checked=#{checked}
WHERE shop_id= #{currShopId} and
    sale_id=#{saleId} and
    pm_id=#{pmId}

UPDATE tb_sale_used_device
SET ud_st=#{state}
WHERE shop_id= #{currShopId} and
    sale_id=#{saleId} and
    ud_id=#{udId}

UPDATE tb_sale_card
SET card_st=#{state}
WHERE shop_id=#{currShopId} and
    sale_id=#{saleId} and
    card_id=#{cardId}

UPDATE tb_sale
SET comb_st=#{state}
WHERE shop_id= #{currShopId} and
    sale_id=#{saleId}

UPDATE tb_sale_support
SET sup_st=#{state}
WHERE shop_id = #{currShopId} and
    sale_id=#{saleId} and
    sup_id=#{supId}

mapper.delete:
