mapper.select:
SET @total_sale_count := ( SELECT count(*) FROM tb_sale WHERE shop_id=#{currShopId} and actv_dt like '${date}%' );
with tb as ( SELECT IFNULL(count(*),0) as curr FROM tb_sale_card sc LEFT OUTER JOIN tb_sale sl on sl.sale_id=sc.sale_id WHERE sc.shop_id=#{currShopId} and actv_dt like '${date}%' UNION ALL SELECT IFNULL(count(*),0) as curr FROM tb_sale WHERE shop_id= #{currShopId} and actv_dt like '${date}%' and sd_id is not null UNION ALL SELECT IFNULL(count(*) ,0) as curr FROM tb_sale WHERE shop_id= #{currShopId} and actv_dt like '${date}%' and exsvc_id is not null )
SELECT curr as value, @total_sale_count as total, ( CASE WHEN @total_sale_count = 0 THEN 0 WHEN @total_sale_count != 0 THEN TRUNCATE(((curr/@total_sale_count))*100, 1) END ) as per FROM tb
