mapper.insert:
set @next_shopId := ( select IFNULL(count(*)+1, 1) from tb_shop );
INSERT INTO tb_shop (shop_id, shop_nm, shop_addr, shop_tel, provider) VALUES (@next_shopId, #{shopNm}, #{shopAddr}, #{shopTel},#{provider});
UPDATE tb_account SET curr_shop_id = @next_shopId WHERE curr_shop_id = 0 and id=#{userId};
INSERT INTO tb_staff (shop_id, staff_id, role, approval_st) VALUES (@next_shopId, #{userId}, 1, 1);
set @size1 := ( select IFNULL(count(*),0) from tb_internet_plan where shop_id=@next_shopId );
insert into tb_internet_plan select @next_shopId as shop_id, (@size1 + row_number() over (order by internet_plan_id)) as idx, provider, internet_plan_nm, now() from tb_internet_plan where shop_id = -1;
set @size2 := ( select IFNULL(count(*),0) from tb_tv_plan where shop_id=@next_shopId );
insert into tb_tv_plan select @next_shopId as shop_id, (@size2 + row_number() over (order by tv_plan_id)) as idx, provider, tv_plan_nm, now() from tb_tv_plan where shop_id = -1;
set @size3 := ( select IFNULL(count(*),0) from tb_extra_service where shop_id=@next_shopId );
insert into tb_extra_service select @next_shopId as shop_id, (@size3 + row_number() over (order by exsvc_id)) as idx, provider, exsvc_nm, now() from tb_extra_service where shop_id = -1;
set @size4 := ( select IFNULL(count(*),0) from tb_sup_div where shop_id=@next_shopId );
insert into tb_sup_div select @next_shopId as shop_id, (@size4 + row_number() over (order by sup_div_id)) as idx, sup_div_nm, now() from tb_sup_div where shop_id = -1;
set @size5 := ( select IFNULL(count(*),0) from tb_add_div where shop_id=@next_shopId );
insert into tb_add_div select @next_shopId as shop_id, (@size5 + row_number() over (order by add_div_id)) as idx, add_div_nm, now() from tb_add_div where shop_id = -1;
set @size6 := ( select IFNULL(count(*),0) from tb_comb_tp where shop_id=@next_shopId );
insert into tb_comb_tp select @next_shopId as shop_id, (@size6 + row_number() over (order by comb_tp_id)) as idx, comb_tp_nm, now() from tb_comb_tp where shop_id = -1;

UPDATE tb_account
set curr_shop_id=#{shopId}
where id=#{userId};

INSERT INTO tb_staff
    (shop_id, staff_id, role, approval_st)
VALUES (#{shopId}, #{userId}, 0, 0);

mapper.select:
with base as ( select sh.shop_id, shop_nm, ( SELECT ac.name FROM tb_account ac where ac.id=sf.staff_id ) as reps_nm, ( SELECT ac.pfp FROM tb_account ac where ac.id=sf.staff_id ) as pfp, sf.staff_id as id, br_no, shop_addr, shop_tel, ( select IFNULL(sum(total_cms),0) from tb_sale sl where sl.shop_id=sh.shop_id ) as total_cms, ( select DATE_FORMAT(max(sl.regi_dt), '%Y-%m-%d') from tb_sale sl where sl.shop_id=sh.shop_id ) as last_sale_dt, DATE_FORMAT(sh.regi_dt, '%Y-%m-%d') as regi_dt from tb_shop sh left outer join tb_staff sf on sf.shop_id=sh.shop_id and role=1 ), search as ( select * from base where
( shop_nm like '%${keyword}%' or shop_addr like '%${keyword}%' or shop_tel like '%${keyword}%' or reps_nm like '%${keyword}%' ) and
1=1
order by ${order} ${asc}
limit ${limit}
), result as ( select json_arrayagg(json_object( 'shop_id',shop_id, 'shop_nm',shop_nm, 'shop_addr',shop_addr, 'shop_tel',shop_tel, 'reps_nm',reps_nm, 'br_no',br_no, 'total_cms',total_cms, 'last_sale_dt',last_sale_dt, 'regi_dt',regi_dt, 'pfp',pfp, 'id',id )) from search )
select ( select count(*) from search ) as total_cnt, ( select * from result ) as list
