mapper.insert:
SET @next_todo_id := (
    SELECT IFNULL(max(todo_id),0)+1
    FROM tb_todo
    WHERE shop_id = #{currShopId} and
          todo_dt = #{date}
    );

INSERT INTO tb_todo
(shop_id, todo_dt, todo_id, color, checked, content)
VALUES (#{currShopId}, #{date}, @next_todo_id, #{color}, #{checked}, #{content})

mapper.update:
UPDATE tb_todo
SET color=#{color}
WHERE shop_id= #{currShopId} and
    todo_id=#{todoId} and
    todo_dt=#{date}

UPDATE tb_todo
SET checked=#{checked}
WHERE shop_id= #{currShopId} and
    todo_id=#{todoId} and
    todo_dt=#{date}

UPDATE tb_todo
SET content=#{content}
WHERE shop_id=#{currShopId} and
    todo_id=#{todoId} and
    todo_dt=#{date}
