mapper.select:
SELECT pfp
FROM tb_account
WHERE id = #{id}

mapper.update:
UPDATE tb_account
SET pfp=#{pfp}
WHERE id = #{id}

mapper.select:
SELECT approval_st
FROM tb_emp
WHERE emp_id = #{id}

SELECT id,
       regi_dt
FROM tb_account
WHERE name = #{name}
  AND email = #{email}

SELECT id,
       regi_dt
FROM tb_account
WHERE name = #{name}
  AND tel = #{tel}

mapper.update:
update tb_emp set
shop_id=#{shopId},
approval_st=#{approvalSt} where emp_id=#{empId}

mapper.select:
WITH tb AS (
               SELECT em.*,
                      cp.corp_bp_no AS bp_no
               FROM tb_emp em
                        LEFT OUTER JOIN tb_corp cp ON cp.reps_id = em.emp_id
           )

SELECT *
FROM tb
WHERE ${andSelect}
  AND ${orSearch}
    ${prop}
