mapper.select:
SELECT sf.staff_id as id,
       (
           select name
           from tb_account
           where id=#{userId}
           ) as name
FROM tb_staff sf
WHERE sf.role=1 and
      shop_id=#{shopId}

select pwd
from tb_account
where id=#{id}

mapper.update:
UPDATE tb_account
SET pwd = #{updatePwd}
WHERE id = #{id}
  AND pwd = #{pwd}

mapper.select:
SELECT curr_shop_id
FROM tb_account
WHERE id=#{userId}

SELECT name
FROM tb_account
WHERE id=#{id}

mapper.update:
UPDATE tb_staff
SET approval_st=#{state}
WHERE staff_id=#{staffId} and
      shop_id=#{currShopId}

update tb_account
set curr_shop_id=#{shopId}
where id=#{userId}

UPDATE tb_account
SET nickname=#{nickname}
WHERE   id=#{id}

UPDATE tb_account
SET     pwd=#{pwd}
WHERE id = #{id}

mapper.select:
SELECT (COUNT(*) > 0) as exist
FROM tb_account
WHERE id = #{userId}

SELECT (COUNT(*) > 0) as exist
FROM tb_account
WHERE email = #{email}

SELECT IF(count(*) > 0,TRUE,FALSE)
FROM tb_account
WHERE id = #{id}
  AND (
    tel = #{tel} or
    email = #{email}
    )

SELECT pfp
FROM tb_account
WHERE id = #{id}

mapper.update:
UPDATE tb_account
SET pfp=#{path}
WHERE id = #{id}

mapper.select:
SELECT id,
       regi_dt
FROM tb_account
WHERE name = #{name}
  AND email = #{email}

SELECT id,
       regi_dt
FROM tb_account
WHERE name = #{name}
  AND tel = #{tel}

SELECT *
FROM tb_account
WHERE ${andSelect}
  AND ${orSearch}
    ${prop}

mapper.update:
update tb_staff set
shop_id=#{shopId},
approval_st=#{approvalSt} where user_id=#{staffId}

mapper.select:
SELECT count(*)
FROM tb_staff
WHERE shop_id= #{currShopId}
