mapper.update:
update tb_staff
set start_dt=#{startDt}
where shop_id=#{currShopId} and
      staff_id=#{userId}

mapper.select:
with users as ( select ac.*, sf.role, IF(sf.role = 1, ac.br_no, '') as br_no, sf.approval_st, sf.shop_id, sh.shop_nm from tb_staff sf left outer join tb_shop sh on sh.shop_id=sf.shop_id left outer join tb_account ac on ac.id=sf.staff_id where user_st=#{userSt} ), search as ( select * from users where
( name like '%${keyword}%' or tel like '%${keyword}%' or email like '%${keyword}%' or br_no like '%${keyword}%' ) and
1=1
limit ${limit}
), result as ( select json_arrayagg(json_object( 'id', id, 'shop_id',shop_id, 'name',name, 'pfp',pfp, 'email',email, 'tel',tel, 'br_no',br_no, 'regi_dt',date_format(regi_dt, '%Y-%m-%d'), 'last_login_dt',last_login_dt, 'role',role, 'approval_st',approval_st, 'shop_nm',shop_nm )) from search )
select ( select count(*) from users ) as total_cnt, ( select * from result ) as list

SELECT sf.staff_id as id,
       (
           select name
           from tb_account
           where id=#{userId}
           ) as name
FROM tb_staff sf
WHERE sf.role=1 and
      shop_id=#{shopId}

select pwd
from tb_account
where id=#{id}

mapper.update:
UPDATE tb_account
SET pwd = #{updatePwd}
WHERE id = #{id}
  AND pwd = #{pwd}

mapper.select:
WITH sale as ( SELECT sf.staff_id as id, sf.role, ac.name, ac.tel, ac.email, sf.start_dt, ac.last_login_dt, sf.shop_id, sf.approval_st from tb_staff sf left outer join tb_account ac on ac.id = sf.staff_id where sf.shop_id = #{currShopId} ), search as ( SELECT * from sale WHERE
( name like '%${keyword}%' or tel like '%${keyword}%' ) and
1=1
limit ${limit}
), result as ( SELECT json_arrayagg(json_object( 'id',id, 'role',role, 'name',name, 'tel',tel, 'email',email, 'last_login_dt',last_login_dt, 'start_dt',start_dt, 'shop_id',shop_id, 'approval_st',approval_st )) from search )
select ( select IFNULL(count(*),0) from sale ) as total_cnt, ( select * from result ) as list

SELECT curr_shop_id
FROM tb_account
WHERE id=#{userId}

SELECT name
FROM tb_account
WHERE id=#{id}

mapper.update:
UPDATE tb_staff
SET approval_st=#{approval}
WHERE staff_id=#{userId} and
      shop_id=#{shopId}

update tb_account
set curr_shop_id=#{shopId}
where id=#{userId}

UPDATE tb_account
SET nickname=#{nickname}
WHERE   id=#{id}

UPDATE tb_account
SET br_no=#{brNo},
    corp_nm=#{corpNm}
WHERE id=#{userId}

UPDATE tb_account
SET     pwd=#{pwd}
WHERE id = #{id}

mapper.select:
SELECT IF(count(*) > 0, true, false)
FROM tb_account
WHERE id = #{userId}

SELECT (COUNT(*) > 0) as exist
FROM tb_account
WHERE email = #{email}

SELECT IF(count(*) > 0,TRUE,FALSE)
FROM tb_account
WHERE id = #{id}
  AND (
    tel = #{tel} or
    email = #{email}
    )

SELECT pfp
FROM tb_account
WHERE id = #{id}

mapper.update:
UPDATE tb_account
SET pfp=#{path}
WHERE id = #{id}

mapper.select:
SELECT id,
       regi_dt
FROM tb_account
WHERE name = #{name}
  AND email = #{email}

SELECT id,
       regi_dt
FROM tb_account
WHERE name = #{name}
  AND tel = #{tel}

SELECT *
FROM tb_account
WHERE ${andSelect}
  AND ${orSearch}
    ${prop}

mapper.update:
update tb_staff set
shop_id=#{shopId},
approval_st=#{approval} where user_id=#{staffId}

mapper.select:
SELECT count(*)
FROM tb_staff
