mapper.update:
UPDATE tb_account
SET pwd = #{updatePwd}
WHERE id = #{id}
  AND pwd = #{pwd}

mapper.select:
SELECT name
FROM tb_account
WHERE id=#{id}

mapper.update:
UPDATE tb_staff
SET approval_st=#{state}
WHERE staff_id=#{staffId} and
      shop_id=(
          SELECT curr_shop_id
          FROM tb_account
          WHERE id=#{userId}
          )

UPDATE tb_account
SET nickname=#{nickname}
WHERE   id=#{id}

UPDATE tb_account
SET     pwd=#{pwd}
WHERE id = #{id}

mapper.select:
SELECT (COUNT(*) > 0) as exist
FROM tb_account
WHERE id = #{userId}

SELECT (COUNT(*) > 0) as exist
FROM tb_account
WHERE email = #{email}

SELECT IF(count(*) > 0,TRUE,FALSE)
FROM tb_account
WHERE id = #{id}
  AND (
    tel = #{tel} or
    email = #{email}
    )

SELECT pfp
FROM tb_account
WHERE id = #{id}

mapper.update:
UPDATE tb_account
SET pfp=#{pfp}
WHERE id = #{id}

mapper.select:
SELECT id,
       regi_dt
FROM tb_account
WHERE name = #{name}
  AND email = #{email}

SELECT id,
       regi_dt
FROM tb_account
WHERE name = #{name}
  AND tel = #{tel}

SELECT *
FROM tb_account
WHERE ${andSelect}
  AND ${orSearch}
    ${prop}

mapper.update:
update tb_staff set
shop_id=#{shopId},
approval_st=#{approvalSt} where user_id=#{staffId}
